msg.from = "Smart Fridge"
msg.payload = "<p>Dear customer,<p> " + msg.payload[index].description
return msg;

receipes = msg.payload

for (var i = 0; i < receipes.length; i++) {
    receipes[i]["ingredients_array"] = receipes[i].ingredients.split(",").map(function (str) {
      return str.trim().toLowerCase();
    });
}

