for (var i = 0; i < ingredients.length; i++) {
    for (var j = 0; j < receipes.length; j++) {
        var item = ingredients[i].name.trim().toLowerCase();
        var required_items = receipes[j]["ingredients_array"];
        var index = required_items.indexOf(item);
        if(index >= 0)
            required_items = required_items.splice(index, 1);
    }
}

recommended_receipes = [];
for (var i = 0; i < receipes.length; i++) {
    if(receipes[i]["ingredients_array"].length === 0){
        delete receipes[i]["ingredients_array"]
        recommended_receipes.push(receipes[i]);
    }

}

msg.payload = recommended_receipes
delete msg["receipes"]

return msg;

msg.topic = "Your fridge is getting too warm!"
msg.from = "Smart Fridge"
msg.payload = "<p>Dear customer,</p> <p>Based on the last hour observation, the average temperature of the fridge is " + msg.payload.average_temperature.toFixed(2) + "°C which is considered unusual reading.</p> <p>Please check your power supply and fridge.</p> <p>Regards, <br> Smart Fridge</p>";
return msg;

function getDate(date){
    return new Date(date.toDateString());
}

function getDaysDifference(d1, d2){
    var timeDiff = d2.getTime() - d1.getTime();
    var diffDays = timeDiff / (1000 * 3600 * 24); 
    return diffDays;
}

var today = new Date();
var ingredients = msg.payload;
var expirying_ingredients = [];

for (var i = 0; i < ingredients.length; i++) {
    var expiry = getDate(new Date(ingredients[i].expiry_date));
    difference = getDaysDifference(today, expiry)

    if(difference <= 1)
        expirying_ingredients.push(ingredients[i].name);

}

msg.payload = expirying_ingredients;

return msg;

